        transition: opacity .5s;
    }
    .fade-enter, .fade-leave-to /* .fade-leave-active below version 2.1.8 */ {
        opacity: 0;
    }
    .art-list-move {
        transition: transform 1s;
    }
    .text-article {
        transition: all .5s;
        display: inline-block;
        margin-right: 10px;
    }

</style>