<script>
    import {Affix} from 'vue-affix';
    import {library} from '@fortawesome/fontawesome-svg-core'
    import {faEye, faEyeSlash, faChevronDown, faChevronUp } from '@fortawesome/free-solid-svg-icons'
    import {FontAwesomeIcon} from '@fortawesome/vue-fontawesome'

    library.add(faEye, faEyeSlash, faChevronDown, faChevronUp)
    export default {
        name: "Const",
        components: {
            Affix, FontAwesomeIcon
        },
        created() {
            window.addEventListener('resize', this.handleResize)
            this.handleResize();
        },
        destroyed() {
            window.removeEventListener('resize', this.handleResize)
        },
        data() {
            let visble = []
            for (let i = 1; i <= 366; i++) {
                visble[i] = false;
            }

            let delEx = "Aquí puedes ver todo el contenido de la Constitución de 1976 que no se incluyó —de ninguna manera— en el proyecto constitucional. Siempre verás el artículo completo y en color negro está el fragmento eliminado."
            return {
                selected: "mark",
                explicative: delEx,
                delEx: delEx,
                widths: 0,
                open: false,
                equEx: "Aquí puedes ver todo el contenido que estaba en la Constitución de 1976 y que se mantiene en el proyecto. Siempre verás el artículo completo y en color negro está el fragmento igual.",
                addEx: "Aquí puedes ver todo el contenido que no está en la Constitución de 1976 y que aparece por primera vez en el proyecto constitucional. Siempre verás el artículo completo y en color negro está el fragmento nuevo.",
                modEx: "Aquí puedes ver todo el contenido de la Constitución de 1976 que en el proyecto se transformó. Puede ser por adición, eliminación o cambio de posición de alguna de sus partes. Siempre verás a la izquierda el texto del proyecto y a la derecha el de la Constitución de 1976. Aparece tachado en rojo lo que se eliminó y en color amarillo lo que se agregó.",
                value1: 41.4,
                value2: 46.6,
                value3: 12,
                value4: 15.4,
                size: 180,
                width: 40,
                add: false,
                equ: false,
                mod: false,
                del: false,
                artvis: visble,
                addStyle: {
                    color: "#00000075"
                },
                modStyle: {
                    color: "#00000075"
                },
                equStyle: {
                    color: "#00000075"
                },
                delStyle: {
                    color: "#00000075"
                }
            };

        },
        computed: {
            sizing: function () {
                if (this.widths < 468)
                    return "#tool1"
                return false
            }
        },
        methods: {
            handleResize() {
                this.widths = window.innerWidth;
            },
            showArt: function (numero) {
                this.artvis.splice(numero, 1, !this.artvis[numero])
            },
            openall: function () {
                this.open = !this.open
                for (let i = 0; i <= 366; i++) {
                    this.artvis.splice(i, 1, this.open)
                }
            },
            changeVisible: function (type) {
                switch (type) {
                    case "mod": {
                        this.explicative = this.modEx;
                        this.mod = true;
                        this.equ = false;
                        this.add = false;
                        this.del = false;
                        this.addStyle = {
                            color: "#00000075"
                        }
                        this.modStyle = {
                            color: "#000000FF"
                        }
                        this.equStyle = {
                            color: "#00000075"
                        }
                        break;
                    }
                    case "add": {
                        this.explicative = this.addEx;
                        this.mod = false;
                        this.equ = false;
                        this.add = true;
                        this.del = false;
                        this.addStyle = {
                            color: "#000000FF"
                        }
                        this.modStyle = {
                            color: "#00000075"
                        }
                        this.equStyle = {
                            color: "#00000075"
                        }
                        break;
                    }
                    case "del": {
                        this.explicative = this.delEx;
                        this.mod = false;
                        this.equ = false;
                        this.add = false;
                        this.del = true;
                        break;
                    }
                    case "equ": {
                        this.explicative = this.equEx;
                        this.mod = false;
                        this.equ = true;
                        this.add = false;
                        this.del = false;

                        this.addStyle = {
                            color: "#00000075"
                        }
                        this.modStyle = {
                            color: "#00000075"
                        }
                        this.equStyle = {
                            color: "#000000FF"
                        }
                        break;
                    }

                }
            }
        }
    }
</script>

